import React, { Component } from 'react';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };
  }

  componentDidMount() {
    fetch("https://sandbox.coinmarketcap.com/v1/global-metrics/quotes/latest", {
      qs: {
        start: 1,
        limit: 100,
        convert: 'USD'
      },
      headers: {
        'X-CMC_PRO_API_KEY': 'e26f45f3-a4d9-41b2-8943-2d3b918441c3'
      },
      json: true,
      gzip: true
    })
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result.items
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    return (
      <div className="App">
        <h1>
          Hello World
        </h1>
      </div>
    );
  }
}

export default App;
