import React, { Component } from 'react';
import TopTable from './market_overview/TopTable'
import MoversTable from './market_overview/MoversTable'

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
        error: null,
        isLoaded: false,
        data: null
    };
  }

  componentDidMount() {

    fetch(`https://min-api.cryptocompare.com/data/top/mktcapfull?limit=100&tsym=USD`, {
      method: 'GET',
      headers: {
        'authorization': `Apikey ${process.env.CRYPTOCOMPARE_API_KEY}`
        },
    })
      .then(res => res.json())
      .then(
        (result) => {
          console.log(result)
          this.setState({
            isLoaded: true,
            data: result.Data
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    if(this.state.data) {
      return (
        <div className="App">
          <h1>
            Crypto Dashboard
          </h1>

          <TopTable data={this.state.data}/>
          <MoversTable data={this.state.data.sort((a, b) => a.RAW.USD.CHANGEPCT24HOUR < b.RAW.USD.CHANGEPCT24HOUR)}/>
        </div>
      );
    } else {
      return (
        <div className="App">
          <h1>
            Loading
          </h1>
        </div>
      )
    }

  }
}

export default App;
